parameters.jsCode:
// Inputs
const companyKey = $json.company_key || '';
const companyProfileKey = $json.company_profile_key || '';
const competitors = $json.competitors || [];

// Fallback paths if no watch_urls are provided
const FALLBACK_PATHS = ['pricing', 'plans', 'products', 'product', 'solutions', 'news', 'press', 'press-releases', 'blog'];

function normBase(url) {
  if (!url) return '';
  return url.replace(/\/+$/, ''); // trim trailing slash
}

function fallbackWatchUrls(site) {
  const base = normBase(site);
  if (!base) return [];
  return FALLBACK_PATHS.map(p => `${base}/${p}`);
}

let out = [];

for (const comp of competitors) {
  // Prefer provided watch_urls; otherwise build from homepage
  let urls = Array.isArray(comp.watch_urls) && comp.watch_urls.length
    ? comp.watch_urls
    : fallbackWatchUrls(comp.website);

  // Ensure we have at least one URL (homepage) if all else fails
  if (!urls.length && comp.website) urls = [normBase(comp.website)];

  // Cap to 3 to keep things light
  urls = urls.slice(0, 3);

  for (const url of urls) {
    out.push({
      json: {
        company_key: companyKey,
        company_profile_key: companyProfileKey,
        competitor_name: comp.name || '',
        competitor_site: comp.website || '',
        watch_url: url
      }
    });
  }
}

// Safety: don't return zero items (downstream expressions may expect data)
if (out.length === 0) {
  // Either enable "Settings â†’ Always Output Data", or emit a minimal row:
  out.push({
    json: {
      company_key: companyKey,
      company_profile_key: companyProfileKey,
      note: 'no-competitor-urls-generated',
      count: 0
    }
  });
}

return out;

