assignments.value:
={{
  (function() {
    const i = $json.intel || {};
    const lines = [];
    lines.push(`## Final Competitor Report`);
    lines.push(`Company: ${i.company_key || 'unknown'}`);
    lines.push(`Date: ${i.date || new Date().toISOString().slice(0,10)}`);
    lines.push('');
    lines.push(`### Findings`);
    (i.findings || []).forEach((f, idx) => {
      lines.push(`- ${idx+1}. ${f.competitor || 'Unknown'} | ${f.url || ''}`);
      if (Array.isArray(f.signals) && f.signals.length) {
        lines.push(`  - Signals: ${f.signals.join('; ')}`);
      }
      if (Array.isArray(f.price_examples) && f.price_examples.length) {
        lines.push(`  - Prices: ${f.price_examples.map(p => `${p.plan||''} ${p.currency||''}${p.price||''}`).join('; ')}`);
      }
      if (f.evidence) {
        lines.push(`  - Evidence: ${f.evidence}`);
      }
    });
    lines.push('');
    if ((i.observations||[]).length) {
      lines.push(`### Observations`);
      (i.observations||[]).forEach(o => lines.push(`- ${o}`));
      lines.push('');
    }
    if ((i.action_plan||[]).length) {
      lines.push(`### Action plan`);
      (i.action_plan||[]).forEach(a => lines.push(`- ${a}`));
      lines.push('');
    }
    return lines.join('\n');
  })()
}}

={{
  (function () {
    const i = $json.intel || {};
    const esc = s => (s||'').replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');

    // ---- 1) Deduplicate today's findings by URL (first occurrence wins)
    const seen = new Set();
    const dedup = [];
    for (const f of (i.findings || [])) {
      const url = (f.url || '').trim();
      if (!url || seen.has(url)) continue;
      seen.add(url);
      dedup.push(f);
    }

    // ---- 2) Priority rules (very simple, in-run only)
    const priority = (f) => {
      const url = (f.url || '').toLowerCase();
      if (/\/pricing|\/plans/.test(url)) return 'High';
      if ((f.signals || []).length)       return 'Medium';
      return 'Low';
    };

    // ---- 3) Optional NEW/SEEN: compare to previous hashes (if provided)
    // If you can pass `$json.prev_hashes = ['amd|https://...','groq|https://...']`
    // from any existing node, this will auto-label NEW/SEEN.
    const prev = new Set(Array.isArray($json.prev_hashes) ? $json.prev_hashes : []);
    const idFor = f => `${(f.competitor||'').toLowerCase()}|${(f.url||'').toLowerCase()}`;
    const label = f => prev.size ? (prev.has(idFor(f)) ? 'SEEN' : 'NEW') : '';

    const rows = dedup.map((f,idx)=>`
      <tr>
        <td style="padding:8px;border:1px solid #ddd">${idx+1}</td>
        <td style="padding:8px;border:1px solid #ddd">
          ${esc(f.competitor||'')}
          ${label(f) ? `<span style="color:#fff;background:#6b46c1;border-radius:6px;padding:2px 6px;margin-left:6px;font-size:12px">${label(f)}</span>` : ``}
        </td>
        <td style="padding:8px;border:1px solid #ddd"><a href="${esc(f.url||'')}" target="_blank">${esc(f.url||'')}</a></td>
        <td style="padding:8px;border:1px solid #ddd">${esc((f.signals||[]).join('; '))}</td>
        <td style="padding:8px;border:1px solid #ddd">${esc((f.price_examples||[]).map(p => `${p.plan||''} ${p.currency||''}${p.price||''}`).join('; '))}</td>
        <td style="padding:8px;border:1px solid #ddd">${esc(f.evidence||'')}</td>
        <td style="padding:8px;border:1px solid #ddd">${priority(f)}</td>
      </tr>
    `).join('');

    const list = arr => arr && arr.length
      ? `<ul>${arr.map(x=>`<li>${esc(x)}</li>`).join('')}</ul>`
      : '<em>None</em>';

    return `
      <div style="font-family:system-ui,Segoe UI,Roboto,Helvetica,Arial,sans-serif;line-height:1.4">
        <h2 style="margin:0 0 8px">Competitor Report</h2>
        <div style="color:#444;margin:0 0 16px">
          <div><strong>Company:</strong> ${esc(i.company_key||'')}</div>
          <div><strong>Date:</strong> ${esc(i.date||new Date().toISOString().slice(0,10))}</div>
        </div>

        <h3 style="margin:16px 0 8px">Findings</h3>
        <table style="border-collapse:collapse;width:100%;font-size:14px">
          <thead>
            <tr>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">#</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">Competitor</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">URL</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">Signals</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">Prices</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">Evidence</th>
              <th style="padding:8px;border:1px solid #ddd;text-align:left">Priority</th>
            </tr>
          </thead>
          <tbody>
            ${rows || `<tr><td colspan="7" style="padding:8px;border:1px solid #ddd"><em>No findings</em></td></tr>`}
          </tbody>
        </table>

        <h3 style="margin:16px 0 8px">Observations</h3>
        ${list(i.observations||[])}

        <h3 style="margin:16px 0 8px">Action plan</h3>
        ${list(i.action_plan||[])}
      </div>
    `;
  })()
}}

